mapper.insert:
INSERT INTO hqls_finance_flow (
    store_id, transaction_no, change_type, change_money, charge_type, pay_type, flow_status, check_status, order_no, oper_person, create_time, remark, dml_time, account_name, account, bank, open_bank, is_delete
) VALUES (
    #{storeId}, #{transactionNo}, #{changeType}, #{changeMoney}, #{chargeType}, #{payType}, #{flowStatus}, #{checkStatus}, #{orderNo}, #{operPerson}, now(), #{remark}, now(), #{accountName}, #{account}, #{bank}, #{openBank}, #{isDelete}        
)

mapper.select:
SELECT f.*, u.store_name,u.user_name as customer_name,u.mobile, CASE WHEN f.check_status = 1 THEN '待审核' WHEN f.check_status = 2 THEN '审核通过' WHEN f.check_status = 2 THEN '审核不通过' ELSE '标志数字不存在' END AS check_status_desc, CASE WHEN f.flow_status = 1 THEN '成功' WHEN f.flow_status = 2 THEN '失败' ELSE '标志数字不存在' END AS flow_status_desc FROM hqls_finance_flow f, ( SELECT c.user_name, c.mobile, s.store_id, s.store_name FROM hqls_user c, hqls_store s, hqls_user_store cs WHERE c.user_id = cs.user_id AND s.store_id = cs.store_id ) u WHERE f.store_id = u.store_id
AND f.store_id = #{storeId}
AND f.change_type = #{changeType}
AND f.flow_status = #{flowStatus}
AND u.user_name LIKE CONCAT('%', #{customerName}, '%')
AND u.mobile LIKE CONCAT('%', #{mobile}, '%')
AND datediff(f.create_time,#{createTime})=0
