mapper.insert:
INSERT INTO hqls_parts (
    parts_code, parts_name, parts_model, parts_unit, parts_spec, parts_brand_id, parts_factory, origin, shelf_life, price, discount, cur_price, remark, is_usable, parts_type_id, create_time, dml_time
) VALUES (
    #{partsCode}, #{partsName}, #{partsModel}, #{partsUnit}, #{partsSpec}, #{partsBrandId}, #{partsFactory}, #{origin}, #{shelfLife}, #{price}, #{discount}, #{curPrice}, #{remark}, #{isUsable}, #{partsTypeId}, #{createTime}, #{dmlTime}        
)

mapper.select:
( SELECT part.parts_id AS `partsId`, part.parts_name AS `partsName`, part.parts_type_id as `partsTypeId`, part.cur_price as partsPrice, (SELECT pic.url FROM hqls_parts_pic AS pic WHERE pic.parts_id = part.parts_id ORDER BY sorting LIMIT 0,1) as `picUrl` FROM hqls_parts AS part WHERE
parts_name LIKE CONCAT('%', #{item}, '%')
) UNION ( SELECT part.parts_id AS `partsId`, part.parts_name AS `partsName`, part.parts_type_id as `partsTypeId`, part.cur_price as partsPrice, (SELECT pic.url FROM hqls_parts_pic AS pic WHERE pic.parts_id = part.parts_id ORDER BY sorting LIMIT 0,1) as `picUrl` FROM hqls_parts AS part LEFT JOIN hqls_parts_model middle ON part.parts_id = middle.parts_id LEFT JOIN hqls_car_model model ON middle.model_id = model.model_id WHERE
model.model_name LIKE CONCAT('%', #{item}, '%')
)
